import React, {Component} from 'react';
import './WaiterButton.css';
import ReactDom from 'react-dom'
import BoxHeaderButton from './BoxHeaderButton';
import Breakfast from './Breakfast';
import Lunch from './Lunch';

class WaiterButton extends Component{
    constructor(props){
        super(props);
            this.state ={
                showBreakfast: false,
                showLunch: true,
            }
            this.BreakfastFunc= this.BreakfastFunc.bind(this);
            this.LunchtFunc= this.LunchFunc.bind(this);    
        }



    breakfastFunc(){
        this.setState({
            ...this.state,
            showBreakfast: false,
            showLunch: true,
        })
    }

    LunchFunc(){
        this.setState({
            ...this.state,
            showBreakfast: false,
            showLunch: true,
        })
    }

    render(){

        return (
            <article className="items">

            <BoxHeaderButton rol = "Desayuno" customOnButtonClick ={this.breakfastFunc}/>
            <BoxHeaderButton rol="Almuerzo" customOnButtonClick={this.LunchFunc}/>
            <div id="boxitem">

            </div>
            </article>
        )
    }
}

export default WaiterButton;
