import React, {Component} from 'react';
import './Waiter.css';
import Button from './Button';
import Breakfast from './Breakfast';
import Lunch from './Lunch';

class Waiter extends Component{
    constructor(props){
        super(props);
            this.state ={
                showBreakfast: false,
                showLunch: true,
            }

            this.BreakfastFunc= this.BreakfastFunc.bind(this);
            this.LunchFunc= this.LunchFunc.bind(this);    
        }



    BreakfastFunc(){
        this.setState({
            ...this.state,
            showBreakfast: true,
            showLunch: false,
        })
    }

    LunchFunc(){
        this.setState({
            ...this.state,
            showBreakfast: false,
            showLunch: true,
        })
    }

    render(){

        return (
            <div className="items">

            <Button rol = "Desayuno" customOnButtonClick ={this.BreakfastFunc}/>
            <Button rol="Almuerzo" customOnButtonClick={this.LunchFunc}/>
            <div id="boxitem">
            {
                this.state.showBreakfast &&
                <Breakfast></Breakfast>
            }
            {
                this.state.showLunch &&
                <Lunch></Lunch>
            }
            </div>
            </div>
        )
    }
}

export default Waiter;
