import React, {Component} from 'react';
import './Waiter.css';
import Button from './Button';
import Breakfast from './Breakfast';
import Lunch from './Lunch';
import {connect} from 'react-redux';
import Client from './Client';

class Waiter extends Component{
    constructor(props){
        super(props);
            this.state ={
                showBreakfast: false,
                showLunch: false,
            }

            this.BreakfastFunc= this.BreakfastFunc.bind(this);
            this.LunchFunc= this.LunchFunc.bind(this);    
        }



    BreakfastFunc(){
        this.setState({
            ...this.state,
            showBreakfast: true,
            showLunch: false,
        })
    }

    LunchFunc(){
        this.setState({
            ...this.state,
            showBreakfast: false,
            showLunch: true,
        })
    }

    render(){

        return (
            <div id="containerMenu">

             <Client/>
             <div id = "dinerName"> 
            {this.props.client.client}
            </div>
            <div id="boxitem">
            <Button rol = "Desayuno" customOnButtonClick ={this.BreakfastFunc}/>
            <Button rol="Almuerzo" customOnButtonClick={this.LunchFunc}/>

            {
                this.state.showBreakfast &&
                <Breakfast></Breakfast>
            }
            {
                this.state.showLunch &&
                <Lunch></Lunch>
            }
            </div>
            </div>
        )
    }
}
const mapStateToProps = (state)=>{
    return {
      ...state
    };
  }

  export default connect(
    mapStateToProps
  )(Waiter);
