import React, {Component} from 'react';
import BoxHeaderButton from './BoxHeaderButton';
import breakfast from '../../data/databreakfast.json'


class Breakfast extends Component {
    constructor(props) {
      super(props);
      this.addItem = this.addItem.bind(this);
    }

    addItem = e  => {
      console.log(e.target);
    }

    breakfast() {
      const breakfast = databreakfast.lunchFood.map((Item) => {
        return (
          <BoxHeaderButton className="itembutton" name={Item.name} options={() => { this.addItem(Item) }} />
        );
      });
      return breakfast;
    }


    render() {
      return (
        <div>
          {this.props.category === 'Hamburgers' ? this.hamburgers() : ''}
          {this.props.category === 'Accompaniments' ? this.accompaniments() : ''}
          {this.props.category === 'Drinks' ? this.drinks() : ''}
        </div>
      );
    }
  }


  export default breakfast;
